/**
 * Immutable replacement of a value at a given index
 */
export declare function replace<T>(index: number, value: T, array: Array<T>): Array<T>;
/**
 * Immutable removal of a value at given index
 */
export declare function remove<T>(index: number, array: Array<T>): Array<T>;
/**
 * Immutable insertion of a value at a given index
 */
export declare function insert<T>(index: number, value: T, array: Array<T>): Array<T>;
